float size =2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;//首先改变中心
    uv*=size;//缩放坐标
    //制作爱心需要用到两个函数,第一个是 (x^2 + y^2 -c )^3 第二个是 x^2*y^3;这个c可以控制心的大小
    vec3 col = vec3(step(pow(pow(uv.x,2.) + pow(uv.y,2.)-4.*fract(iTime*0.5),3.),pow(uv.x,2.)*pow(uv.y,3.)));
    vec4 texture1 = texture(iChannel0,uv/size +0.5);
    vec4 texture2 = texture(iChannel1,uv/size +0.5);
    vec4 color = mix(texture1,texture2,col.x);
    fragColor =color;
}
