float circle(vec2 uv, float r){
    return smoothstep(-0.01,0.01,(length(uv)-r));
}


void mainImage(out vec4 c, in vec2 fc)
{
    vec2 uv = (2.0*fc.xy -iResolution.xy)/iResolution.y;
    float angle = 0.2 + sin(iTime)*0.3+cos(1./iTime);

    vec3 color = vec3(0.0);

    for(float i = 1.0;i<12.0;i+=1.0){
        uv = abs(uv);
    uv -= 0.5;
    uv *= 1.1;
    uv *= mat2(sin(angle),sin(angle),cos(angle),sin(angle));
    uv += 0.5;
    }

    float ci = circle(uv, 0.3+sin(iTime*2.)*0.1);

    color.r = ci;

    c = vec4(color,1.0);
}
