void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
  vec2 m = -1.0 + 2.0 * iMouse.xy / iResolution.xy;

  float a1 = atan(p.y-m.y,p.x-m.x);
  float r1 = sqrt(dot(p-m,p-m));
  float a2 = atan(p.y+m.y,p.x+m.x);
  float r2 = sqrt(dot(p+m,p+m));

  vec2 uv;
  uv.x = 0.2*iTime + (r1-r2)*0.25;
  uv.y = asin(sin(a1-a2))/3.1416;



  vec3 col = vec3(0.0); // texture( iChannel0, 0.125*uv ).zyx;

  float w = exp(-20.0*r1*r1) + exp(-20.0*r2*r2);


	// col.y += iTime;
   //  w = 1.0/w;
     if(p.x > 0.0 && r1 > 0.5)
     {

     col = vec3(1.0);
      w -= 0.55*smoothstep( 0.70,1.0,sin(256.0*uv.x));
  w -= 0.55*smoothstep( 0.70,1.0,sin(256.0*uv.y));
  fragColor = vec4(col+w,1.0);
  }
  else
  {
 // w = exp(20.0*r1*r1) + exp(20.0*r2*r2);
//  w = exp(-20.0*r1*r1) + exp(-20.0*r2*r2);
  w += 0.55*smoothstep( 0.70,1.0,sin(256.0*uv.x));
  w += 0.55*smoothstep( 0.70,1.0,sin(256.0*uv.y));
  w += exp(-20.0*r1*r1) + exp(-20.0*r2*r2);
   w -= exp(-20.0*r1*r1) + exp(-20.0*r2*r2);
  fragColor = vec4(col+w,1.0);
  }
}
