const float pi = 3.1415926;
const float pi2 = 2.*pi;
const float rad = pi/180.;
vec2 fc; 
float mt ;
mat2 rotate(float x){
    float c = cos(x);
    float s = sin(x);
    return mat2(c,s,-s,c);
}
mat3 rotateX(float x){
    mat3 m = mat3(1,0,0,0,1,0,0,0,1);
    m[0].xy *= rotate(x);
    m[1].xy *= rotate(x);
    return m;
}
vec4 v(float v){
    return vec4(v, 0.,0.,0.);
}
vec4 v(float v, float v2){
    return vec4(v, v2, 0.,0.);
}

vec4 vmin(vec4 v1, vec4 v2){
    return v1.x < v2.x ? v1 : v2;
}

vec4 vmin(vec4 v1, float v2){
    return v1.x < v2 ? v1 : v(v2);
}

vec4 vmin(vec4 v1, float v2, float v3){
    return v1.x < v2 ? v1 : v(v2,v3);
}

//////////////////////////////////
//https://iquilezles.org/articles/distfunctions
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
//////////////////////////////////


vec4 dist(vec3 p)
{
    vec4 d = v(100.);
    p.xz *= rotate(iTime);

    p.xz *= rotate(-iTime);

    vec3 tp = p*0.5;

    float nx = ( sin(atan(tp.z,tp.x)) ) ;
    float ny = ( sin(atan(tp.y,tp.x)) );

    float f;
    f = 0.;
    vec3 pk = p*2.0*vec3(1.,1.0,1.0)+vec3(1.);
    float f2 = .18-abs(sin(pk.x)*cos(pk.y)+sin(pk.y)*cos(pk.z)+sin(pk.z)*cos(pk.x))/10.0;
    pk = p*144.0*vec3(1.,1.0,1.0)+vec3(1.);
    f2 += .01-abs(sin(pk.x)*cos(pk.y)+sin(pk.y)*cos(pk.z)+sin(pk.z)*cos(pk.x))/190.0;

    f = clamp(f+.05,0.,3.0);
    f2 = clamp(f2 , 0., .2);

    vec3 pq = p*500.0;
    float fv0 = .001-abs(sin(p.x)*cos(pq.y)+sin(pq.y)*cos(pq.z)+sin(pq.z)*cos(pq.x)+cos(iTime+pq.x))/800.0;

    float s = 500.0;
    d = vmin(d, (length(p)-2.0 ) +  - 0.1   +fv0     , 1.0 );
    d = vmin(d, (length(p)-2.0 ) +(f2-.13)*2.0, 2.0 );

    pk = p*4.0*vec3(1.,1.0,1.0)+vec3(1.);
    f2 = .01-abs(sin(pk.x)*cos(pk.y)+sin(pk.y)*cos(pk.z)+sin(pk.z)*cos(pk.x))/190.0;

    d = vmin(d, (length(p+cos(p*vec3(110.0,80.0,.0))*0.006-vec3(0.0,0.,-2.0))-.12 ), 3.0 );

    d = vmin(d, (length(p-vec3(30.0,0.,-2.0))-3.0 ) +(f2-.13)*2.0, 2.0 );

    d = vmin(d, (length(p-vec3(-80.0,0.,-2.0))-22.0 ) +(f2-.13)*2.0, 6.0 );

    d = vmin(d, (length(p-vec3(-180.0,0.,-2.0))-18.0 ) +(f2-.13)*2.0, 7.0 );
    d = vmin(d, (sdTorus((p-vec3(-180.0,00.,-2.0))*rotateX(0.5)*vec3(.25,1.0,0.25),vec2(8.,2.0))-1.0 ) , 8.0 );


    d = vmin(d, (length(p-vec3(400.0,0.,-2.0))-150.0 ), 5.0 );

    vec3 c = vec3(10.0*((1.0 - mt)*.7+.3));

    vec3 q = mod((p+c/2.0)+0.5*c,c)-0.5*c;
    d = vmin(d, max((length(q)-.3), -(length(p-vec3(0.0,0.,.0))-1540.0 )) , 9.0 );

    d = vmin(d, max((length(q-vec3(.2,0.2,0.0))-.2), -(length(p-vec3(0.0,0.,.0))-1540.0 )) , 9.0 );
    d = vmin(d, max((length(q+vec3(.2,0.2,0.0))-.2), -(length(p-vec3(0.0,0.,.0))-1540.0 )) , 9.0 );

    return d;
}
vec3 getNormal(vec3 p)
{
    vec2 d = vec2(0., 0.01);
    float x = dist(p-d.yxx).x;
    float y = dist(p-d.xyx).x;
    float z = dist(p-d.xxy).x;
    return normalize(vec3(x,y,z)-dist(p).x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime+39.0;
    vec2 uv = fragCoord/iResolution.xy;
    fc = uv;
    vec2 pc = (uv-.5)*vec2(1, iResolution.y/iResolution.x);
    vec3 p = vec3(0,0.,-10);
    vec3 d = normalize(vec3(pc,1.0));

    p = vec3(0,0.,-2.2)+vec3(0,0.,-2550)* pow(smoothstep(0.,40.0,fract(time/40.0)*40.0),2.0);

    d = mix(d, normalize(d-vec3(0.,0.,0.995)) , 
        smoothstep( 22.,35.0, fract(time/40.0)*40.0)  
                *
        (1.0 - smoothstep( 33.,35.0, fract(time/40.0)*40.0) )
    );

    d = mix(d, normalize(d-vec3(0.,0.,0.95)) , 
        smoothstep( 38.,40.0, fract(time/40.0)*40.0)  
                *
        (1.0 - smoothstep( 40.,43.0, fract(time/40.0)*40.0) )
    );

    mt =  smoothstep( 33.,37.0, fract(time/40.0)*40.0) ;

    vec3 c;


    {
    ///////////////////////////////////////////////////
        // https://www.shadertoy.com/view/XlfGRj
        vec2 uv=fragCoord.xy/iResolution.xy-.5;
        uv.y*=iResolution.y/iResolution.x;

        vec3 v=vec3(0.);
        vec3 p=vec3(1.,.52,.5)+.1*d;
        float pa,a;
        for (int i=0; i<25; i++) { 
            p=abs(p)/dot(p,p)-0.5; 
            a+=abs(length(p)-pa);  
            pa=length(p);
        }
        a*=a*a; 
        v+=vec3(0.0002*a*.01)*vec3(1.,0.9,1.5); 
        c = v;
     ///////////////////////////////////////////////////
    }


    float t = 0.;
    vec4 q;
    for (int i = 0 ; i < 60; i++)
    {
        q = dist(p);
        float h=q.x; 
        if (h < 0.01)
         break;
        t+=h;
        p += d*h;
    }

    if (t < 1150.0)
        c *= 0.0 ;

    if (q.y == 1.0)
    {
        c += ( getNormal(p).z )*vec3(0.1,.5,1.);
    } 
    if (q.y == 2.0)
    {
        c += ( getNormal(p).z )*vec3(0.,.8,0.5);
    }
    if (q.y == 3.0)
    {
        c += ( getNormal(p).z )*vec3(0.7,0.5,0.1);
    }
    if (q.y == 4.0)
    {
        c += ( getNormal(p).z )*vec3(0.9,0.8,0.1);
    }  
    if (q.y == 5.0)
    {
        vec3 pk = vec3(fc.xy*100.0,1.0);
        pk = p/10.0;
        float f2 = .4-abs(sin(pk.x)*cos(pk.y)+sin(pk.y)*cos(pk.z)+sin(pk.z)*cos(pk.x))/1.0;
        c += (( getNormal(p).z*3.0 ))*vec3(0.9,0.8,0.1)*3.;
        c = clamp(c,vec3(0.),vec3(1.));
        c += abs(cos(f2+iTime)*0.25+.75)*vec3(1.,.5,.5)*1.;
    }  
    if (q.y == 6.0)
    {

        c += ( getNormal(p).z )*vec3(0.9,0.5,0.1)*1.;
    } 
    if (q.y == 7.0)
    {

        c += ( getNormal(p).z )*vec3(0.4,0.5,1.1)*1.;
    } 
    if (q.y == 8.0)
    {

        c += ( getNormal(p).z )*vec3(0.5,0.9,0.9)*1.;
    } 
    if (q.y == 9.0)
    {
        c += ( getNormal(p).z/(t/50.0) )*vec3(1.)*1.;
    } 

    c = mix(c, vec3(0.7, 0.5, 0.1), smoothstep( 35.,40.0, fract(time/40.0)*40.0)  );
    fragColor = vec4(c,1.0);
}
