// color variant of https://shadertoy.com/view/NltXRl

void mainImage(out vec4 O,  vec2 U)
{
    vec2 z = iResolution.xy;
         U = (z = (U+U - z) / z.y) -.2;
	z.x += 2.;  
    U.x -= .6;
    U *= mat2(z,-z.y,z) / dot(U,U);
    U = log(length(U+=.5)) + atan(U.y, U.x) * vec2(1.592, -.637);
    U.y -= iTime/4.;
    z = fract(U+U)-.5;
    O = ( ( z.y<0.? -z.y: .25 ) - abs(z.xxxx) )
        / fwidth(length(1.4*z));

    U = floor(U*4.) +vec2(z.x<0.,z.y>0.);                           // arrow Id
    O = min(O,1.) * ( .6 + .6 * cos( U.x+8.*U.y +iTime + vec4(0,23,21,0)) ); // hue
}
