void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 텍스쳐 UV
    vec2 uv = fragCoord/iResolution.xy;

    float Time = iTime*0.025f;
    vec2 AnimUV1 = Time+uv.xy;
    vec2 AnimUV2 = vec2(Time+(-uv.x), Time+uv.y);

    vec2 GetAnimUV1 = texture(iChannel0, AnimUV1).xy;
    vec2 GetAnimUV2 = texture(iChannel0, AnimUV2).xy;

    vec2 FinalAnimUV = GetAnimUV1 + GetAnimUV2;
    float NoiseRef = texture(iChannel0, FinalAnimUV).x;

    fragColor = mix(vec4(0.0f, 0.5f, 1.0f, 1.0f), vec4(1.0f, 1.0f, 1.0f, 0.3f), NoiseRef);
}
