uint seed = 0u;
void hash(){
    seed ^= 2747636419u;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
}
void initRandomGenerator(vec2 fragCoord){
    seed = uint(fragCoord.y*iResolution.x + fragCoord.x)+uint(iFrame)*uint(iResolution.x)*uint(iResolution.y);
}

float random(){
    hash();
    return float(seed)/4294967295.0;
}



vec3 GetPixel(vec2 fragCoord)
{
  return texture(iChannel0, fragCoord/iResolution.xy).xyz;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        // Time varying pixel colr

    vec3 col;
     //if(mod(iTime, 2.) <= 0.1)
     if(iFrame ==0)
    {
        if(fragCoord.x <iResolution.x)
        {
                initRandomGenerator(fragCoord);
                if(random()>=0.9)col = vec3(1.);
        }

    }    
    else
    {
        int num = 0;

        for(float x = -1.; x<= 1.; x++)
        {
            for(float y = -1.; y<= 1.; y++)
            {
                if(GetPixel(fragCoord + vec2(x,y)).x >= 0.5 && (x !=0. || y!=0.))num++;
            }
        }

        if(num<2 || num >3)col = vec3(0.);
        if((num == 2 || num == 3)&&GetPixel(fragCoord).x > 0.5)col = vec3(1.);
        if(num==3&&GetPixel(fragCoord).x < 0.5)col = vec3(1.,0.,1.);
    }

    if(texture(iChannel1, vec2(1.-uv.x, uv.y)).x >.3 && 
    texture(iChannel1, vec2(1.-uv.x, uv.y)).y >.3 &&
    texture(iChannel1, vec2(1.-uv.x, uv.y)).z >.3)col = vec3(1.);

    fragColor = vec4(col,1.0);




}
