#define S(v) smoothstep(-aa.y,aa.y, v)

vec3 stars(vec2 uv, vec2 aa) {
 uv = (uv + iTime*0.03) * 8.0;
 aa *= 8.0;
 float v = voronoi(uv);

 float stars = S(0.07-v);
 float glow = 1.0 - pow(smoothstep(0.07-aa.y,1.0,v), 0.1);

 return vec3( max(stars,glow) );
}

float vignette(vec2 uv, float vign) {
 vec2 suv = smoothstep(1.0,0.999-vign,abs(uv*2.0-1.0));
 return suv.x * suv.y;
}

vec3 logo(vec2 uv, vec2 aa, float time) {
 vec2 wuv = uv;
 wuv.x += time*0.3;
 float wav = sin(wuv.x*3.1415*6.0);
 wav = (wav * 0.03 + uv.y) - 0.3;
 float wav1 = S(wav);

 //float day_amnt = smoothstep(
 // 11.0,
 // 10.0,
 // abs((daytime.x+(daytime.y/60.0))-12.0));
 float day_amnt = 1.0;
 day_amnt *= 1.0 - exp(-time*2.0);
 float rise = mix(-3.0,0.25,day_amnt);

 vec2 buv = uv-vec2(0.25,rise+0.1);
 float grad = dot(buv,buv);
 float ball = S(length(buv)-0.2);
 float wotr = invert(wav1,grad);
 wotr = invert(wotr,1.0-ball);

 float ntrans = smoothstep(2.0,1.0,grad);

 vec3 colors = mix(
  vec3(0.0,0.5,1.0),
  vec3(1.0),
  wotr
 );

 vec3 star = mix(
  stars(uv, aa),
  vec3( 1.0 ),
  smoothstep(aa.y,0.0,wav) *
  smoothstep(0.05,0.0,-wav)
 );

 colors = mix(
  star,
  colors,
  ntrans
 );

 colors *= vignette(uv,(1.0-day_amnt)*0.4);

 return colors;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = 1.0/max(iResolution.x,iResolution.y);
    vec2 uv = (fragCoord.xy-iResolution.xy*0.5) * m;
    vec2 yuv = uv*5.0;
    vec2 yuvn = yuv*0.5+0.5;

    vec2 aa = 1.5/iResolution.xy;

    vec3 logo = logo(yuvn, aa, iTime);

    fragColor.rgb = vec3( logo );
    fragColor.a = 1.0;
}

// Hash without Sine
// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}


// https://iquilezles.org/articles/smoothvoronoi/
float voronoi( in vec2 x )
{
    vec2 p = floor( x );
    vec2  f = fract( x );

    float res = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 b = vec2( i, j );
        vec2  r = vec2( b ) - f + hash22( p + b );
        float d = dot( r, r );

        res = min( res, d );
    }
    return sqrt( res );
}

float invert(float x, float a) {
 return mix(x, 1.0-x, a);
}

float circ_in(float u) {
 return sqrt(1.0-u*u)-1.0;
}

float parsin(float u) {
 u /= 3.1415;
 float uf = fract(u);
 float ui = mod(floor(u),2.0);
 uf = abs(uf*2.0-1.0);
 uf = sqrt(1.0-uf*uf);
 uf *= float(ui < 1.0)-0.5;

 return uf;
}
