void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
  vec3 col = vec3(0);
  const float one1 = 1.0;
  float one2 = 1.0;
  float one3 = max(1.0,-iTime);

  const float zero1 = 0.0;
  float zero2 = 0.0;
  float zero3 = max(0.0,-iTime);

  // Nvidia/Linux/FF+Chrome: magenta/cyan
  // Nvidia/Windows/Chrome/Opengl: magenta/cyan
  // Nvidia/Windows/Chrome/D3D: magenta/black
  // Nvidia/Windows/Edge/Opengl+D3D: magenta/black
  // iPhone 5s: yellow/white
  // AMD R580/Windows/Chrome+FF: magenta/black
  // AMD 7480D/Linux/FF: white/cyan
  // Intel/Windows/FF/D3D: magenta/black
  // Intel/Linux/FF+Chrome: white/cyan
  // Raspberry Pi: white/cyan
  // Pixel 4a/Samsung A5 Chrome: white/black
  if (uv.x < 0.0) {
    col.r = float(isnan(zero1/zero1));
    col.g = float(isnan(zero2/zero2));
    col.b = float(isnan(zero3/zero3));
  } else {
    col.r = float(isnan(sqrt(-one1)));
    col.g = float(isnan(sqrt(-one2)));
    col.b = float(isnan(sqrt(-one3)));
  }

  fragColor = vec4(col,1);
  //fragColor = vec3(col);
}
