void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv - vec2(0.5);
    //float time = sin(iTime);
    float time = 1.0;

    vec4 color = vec4(uv.y * -1.0) * mix(vec4(0.0, 1.0, 1.0, 1.0), vec4(1.0, 1.0, 0.0, 1.0), fragCoord.x / iResolution.x);

    for (float i = 0.0; i < 100.0; i = i + 1.0) {
        float buf = abs(uv.y * (2.1 + i * 0.6) - sin(uv.x * PI2 * 2.0 * (time + i * 0.1)));
        buf = min(1.0, buf) * 60.0;

        if (buf < 0.6) {
          color = mix(vec4(1.0, 0.0, 0.0, 1.0), vec4(0.0, 0.0, 1.0, 1.0), fragCoord.x / iResolution.x);
          // color = vec4(0.0);
          break;
        }
    }

    fragColor = color ;
}

#define PI 3.14159265359
#define PI2 6.28318530718

vec4 someFunction( vec4 a, float b )
{
    return a+b;
