void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (-iResolution.xy + 2. * gl_FragCoord.xy) / iResolution.y; // -1 <> 1 by height

    vec2 p_mouse = (-iResolution.xy + 2. * iMouse.xy) / iResolution.y;


    vec2 displace = (p - p_mouse) * pow(1./max( length(p - p_mouse), .25) * .3, 3.);
    vec3 col = texture(iChannel0, p - displace).rgb;
//    col = texture(iChannel0, p).rgb;
    col = col * clamp(0., 1., pow(length(p - p_mouse) + 0.68, 35.));
    fragColor = vec4(col,1.0);
}
