renderpass.code:
vec2 rot(vec2 v,float a) {
    float s = sin(a), c = cos(a);
    return mat2(c,s,-s,c)*v;
}

void part(inout vec3 u,float a,float b,float c,float d) {
    u.z = min(u.z,max(step(d,abs(b+u.y)),step(c,abs(a-u.x))));
}

void mainImage(out vec4 fc,in vec2 fn) {
    vec2 uv = rot((fn-.5*iResolution.xy)/iResolution.y*sin(iTime*.5),iTime*.2);
    float s[] = float[](0.0,0.0,.25,.05,0.,0.,.05,.25,-.125,.2,.125,.05,-.2,-.125,.05,.125,.2,.125,.05,.125,.125,-.2,.125,.05);
    for (float i = 1.; i <= 6.; i += 1.) {
        vec3 u = vec3(rot(fract(uv*=2.)-.5,iTime*i*sign(mod(floor(uv).x,2.)*2.-1.))*1.47,1.);
        for(int j = 0; j < 24; j+= 4) part(u,s[j],s[j+1],s[j+2],s[j+3]);
        fc.rgb += u.z*mix(vec3(2.,0.,0.),vec3(max(floor(.35/length(u.xy)),.1/u.z)),.7);
    }
}
