const float smoothing = 0.15;
const float thresholdSensitivity = 0.14;
const vec3 colorToReplace = vec3(0.0, 0.60, 0.17);

vec3 rgb2YCrCb(vec3 o) {
    float Y = 0.2989 * o.r + 0.5866 * o.g + 0.1145 * o.b;
    float cr = 0.7132 * (o.r - Y);
    float cb = 0.5647 * (o.b - Y);
    return vec3(cr, Y, cb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 bgColor = texture(iChannel0, uv);
    vec4 fgColor = texture(iChannel1, uv);


    vec3 color = rgb2YCrCb(fgColor.rgb);
    vec3 mask = rgb2YCrCb(colorToReplace.rgb);

    float blendValue = 1.0 - smoothstep(thresholdSensitivity, thresholdSensitivity + smoothing, distance(color.rb, mask.rb));
    // Output to screen
    fragColor = mix(fgColor, bgColor, blendValue);
}
