void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.y;

    uv-=.5;

    vec2 uv2 = uv;

    uv.x += sin(uv.y) * 100.;
    uv.y - sin(uv.x);

    vec3 col = vec3(uv.x);
    col.r = sin(uv.x*3.14*10.+iTime);
    col.g = sin(uv.x*3.14*9.+iTime);
    //col.b = sin(uv.y*3.14*10.+iTime);
    col.b = sin(uv.x*3.14*9.+iTime*.9)+sin(uv2.x*3.14*10.+iTime);
    //col.rb = step(mouse.xy, col.rb);
    //col.b = fract(uv.x*10.);
    col = vec3(sin(uv.x*3.14*9.+iTime*.9)+sin(uv2.x*3.14*10.+iTime));
    col.b = sin(uv.x*3.14*10.+iTime)*sin(uv2.x*3.14*9.+iTime);
    col.r = sin(uv.x*3.14*10.+iTime)*sin(uv2.y*3.14*10.+iTime);
    col.rgb = vec3(col.r*col.g*col.b+.4);

    fragColor = vec4(col, 1.0);
}
