// noise function
float randomNoise(float x, float y)
{
    return fract(sin(dot(vec2(x, y), vec2(12.9898, 78.233))) * 43758.5453);
}

float _Indensity = 0.01;
float _Amplitude = 1.5;
float _Amount = 0.3;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float splitAmout = (1.0 + sin(iTime * 6.0)) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 16.0) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 19.0) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 27.0) * 0.5;
    splitAmout = pow(splitAmout, _Amplitude);
    splitAmout *= (0.05 * _Amount);

    vec3 finalColor;
    finalColor.r = texture(iChannel0, vec2(uv.x + splitAmout, uv.y)).r;
    finalColor.g = texture(iChannel0, uv).g;
    finalColor.b = texture(iChannel0, vec2(uv.x - splitAmout, uv.y)).b;

    finalColor *= (1.0 - splitAmout * 0.5);

    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
