void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 5.*(fragCoord/iResolution.xy-0.5);
    uv.y*=iResolution.y/iResolution.x;

    float count=texture(iChannel0,vec2(0.5)).x;
    vec2 anime=vec2(cos(count*2.5),sin(count*2.5));
    vec2 anime2=vec2(cos(count*2.5+3.14),sin(count*2.5+3.14));    

    float aka=length(uv-anime);
    float ao=length(uv-anime2);

    vec3 col=mix(vec3(1.,0.,0.),vec3(1.),smoothstep(0.2,0.21,aka));
         col=mix(vec3(0.,0.,1.),col,smoothstep(0.2,0.21,ao));

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float r=texture(iChannel0,uv).x;
    r=r+0.01;
    fragColor = vec4(r);
}
