void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    int t = int(iTime) % 100;
    float tt = float(t) / 100.0;
    float r = 0.07;
    vec3 col;
    float umn = 1.0;
    float del = 0.0;
    t = int(iTime) % 6;
    float rast = sqrt((0.5 - uv.x) * (0.5 - uv.x) + (0.5 - uv.y) * (0.5 - uv.y));
    if(t < 3){
        umn = float(int(iTime * 100.0) % 301)/ 300.0;
        r = 0.07 * umn;

    }
    else{
        del = float(int(iTime * 100.0) % 301)/ 300.0;

    }
    col = vec3((1.0 / rast / 10.0* umn) - del, (1.0 / rast / 10.0 * umn) / 2.0 - del, 0.0);
    if(rast < r){
        col = vec3(1.0 - del, 0.8 - del, 0.0);
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
