void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;

    float r = .8;
    float value = length(uv) - r;
    if (abs(value) <= 0.01) {
        fragColor = vec4(abs(value) * 100.);
    } else {
        fragColor = vec4(1);
    }
}
