const int MAX_I = 1;

float gd(float n, int pos) {
  return mod(round(n/pow(10., float(pos))), 10.);
}

float calc(float n) {
    float fi = 0.;
    vec3 m = vec3(gd(n, 2), gd(n, 1), gd(n, 0));
    for(int i = 0; i <= MAX_I; i++) {
        m = abs(m - m.zyx);
        fi = float(i);
        if (length(m) == 0.) {
            break;
        }
    }
    return fi/float(MAX_I);
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = 1./max(mod(round(iTime/1.71), 10000.), 1.) * max(abs(iMouse.x), 1.) * (2. * u - Res);
    O = vec4(calc(U.x * U.y));
}
