//CLICK MOUSE AND MOVE


#define circle length(o-M)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage(out vec4 c,in vec2 o){

  c=vec4(0);
  vec3 m,m1,m2,m3,m4,m5,universe;
  vec2 M,R=iResolution.xy;o-=R/2.0,o*=4./3.; vec2 oo=o/(1.0+0.25*cos(iTime*radians(60.0)));
  M=((iMouse.x+iMouse.y>0.0)?iMouse.xy/360.0*R.y:vec2(R.x/2.3,R.y/2.2075));
  float r=o.x*o.x+o.y*o.y+iTime*radians(180.0);

  vec2 O1=(oo+M-R/2.);float g1=abs(O1.x)+abs(O1.y)+abs(O1.y*sqrt(2.)/2.0+O1.x*sqrt(2.)/2.0)+abs(-O1.y*sqrt(2.)/2.0+O1.x*sqrt(2.)/2.0);
  vec2 O2=(oo-M+R/2.);float g2=abs(O2.x)+abs(O2.y)+abs(O2.y*sqrt(2.)/2.0+O2.x*sqrt(2.)/2.0)+abs(-O2.y*sqrt(2.)/2.0+O2.x*sqrt(2.)/2.0);
  vec2 O3=(oo-vec2(M.y,R.y)+R.yx/2.);float g3=abs(O3.x)+abs(O3.y)+abs(O3.y*sqrt(2.)/2.0+O3.x*sqrt(2.)/2.0)+abs(-O3.y*sqrt(2.)/2.0+O3.x*sqrt(2.)/2.0);
  vec2 O4=(oo+vec2(M.y,R.y)-R.yx/2.);float g4=abs(O4.x)+abs(O4.y)+abs(O4.y*sqrt(2.)/2.0+O4.x*sqrt(2.)/2.0)+abs(-O4.y*sqrt(2.)/2.0+O4.x*sqrt(2.)/2.0);

  m1=cos(r)*vec3(1,0,0);
  m2=cos(r)*vec3(1,0,1);
  m3=cos(r)*vec3(0,1,0);
  m4=cos(r)*vec3(1,1,0);
  m5=cos(r)*vec3(0,1,1);
  m=cos(r)*vec3(1,1,1)+0.5;

  c=vec4(m-0.5,1.);
  if(g1<R.x/2.0)c=vec4(m2,1.);
  if(g2<R.x/2.0)c=vec4(m1,1.);
  if(g3<R.x/2.0)c=vec4(m4,1.);
  if(g4<R.x/2.0)c=vec4(m3,1.);
  if(int(g1/R.x*22.0)==int(640.0/64.0))c=vec4(m*0.1,1.0);
  else if(int(g2/R.x*22.0)==int(640.0/64.0))c=vec4(m*0.1,1.0);
  else if(int(g3/R.x*22.0)==int(640.0/64.0))c=vec4(m*0.1,1.0);
  else if(int(g4/R.x*22.0)==int(640.0/64.0))c=vec4(m*0.1,1.0);
  else if(g1<R.x/2.0&&g2<R.x/2.0&&g3<R.x/2.0&&g4<R.x/2.0)c=vec4(m+0.5,1.0);
  else if(g3<R.x/2.0&&g1<R.x/2.0)c=vec4(m5,1.0);
  else if(g2<R.x/2.0&&g1<R.x/2.0)c=vec4(m5,1.0);
  else if(g2<R.x/2.0&&g3<R.x/2.0)c=vec4(m5,1.0);
  else if(g4<R.x/2.0&&g1<R.x/2.0)c=vec4(m5,1.0);
  else if(g4<R.x/2.0&&g2<R.x/2.0)c=vec4(m5,1.0);
  c*=cos(iTime*radians(60.0))*0.5+2.0;
}
