//Made using https://www.shadertoy.com/view/MdKfDh (minimum changes)
vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

    //The output must be between 0.0 and 1.0
	return -1.0 + 2.0*fract(sin(p) * 43758.5453123);
}

float noise( in vec2 p ) { //Simplex noise
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y) * K1 );

    vec2 a = p - i + (i.x+i.y) * K2;
    vec2 o = step(a.yx,a.xy);
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

#define OCTAVES 6
float fbm (in vec2 st) {

    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;


    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.; //lacunarity: steps in which we increment de frequencies
        amplitude *= .5; //gain: decreases the amplitude
    }
    return value * 0.5 + 0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    #define fireMovement 		vec2(-0.1, -0.5)
    #define distortionMovement	vec2(-0.01, -0.2)
    #define distortionStrength	0.1

    float color = 0.;
    //Normalize fragment coordinates
    vec2 uv = fragCoord.xy/iResolution.xy;

    //These variables are used to modify the speed at which the texture moves
    float strength = floor(uv.x+1.);
    float t = max(2.,1.25*strength)*iTime*0.75;

    color += fbm(uv * vec2(1.0, 0.3) + distortionMovement * t);
    vec2 displacement = clamp((vec2(color) - .5) * distortionStrength, -1., 1.);
    uv += displacement;

    vec2 uvT = (uv * vec2(1.0, 0.5)) + fireMovement * t * 0.55;
    float col = pow(fbm(8.0 * uvT), 1.0);

    float gradient = pow(1.0 - uv.y, 2.0) * 5.;
    float finalNoise = col * gradient;

    vec3 colorFinal = finalNoise * vec3(col, col*col*col, col*col*col*col*col*col*col*col);
    colorFinal = clamp(colorFinal, 0., 1.);
    fragColor = vec4(colorFinal, 1.0);
}
