vec2 fixUV(in vec2 c)
{
    return 3. * (2.0 * c - iResolution.xy) / min(iResolution.x, iResolution.y);
}

float funcHeart(vec2 uv)
{
    return pow(uv.x, 2.) + pow(uv.y - sqrt(abs(uv.x)), 2.) * abs(sin(iTime));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fixUV(fragCoord);

    // Time varying pixel color
    vec3 color = vec3(funcHeart(uv + vec2(1., 0.)));
    color = mix(color, vec3(1., 0., 0.), vec3(funcHeart(uv - vec2(1., 0.))));
    color = mix(vec3(0., 0, 0), vec3(1., 0, 0), color.x);

    // Output to screen
    fragColor = vec4(color,1.0);
}
