bool is_stripe(float x) {
  float stripes = 16.0;
  float divisor = 1.0 / stripes;
  float mod_x = mod(x, divisor);
  float stripe_width = x * divisor / 1.0;
  float mid_divisor = divisor / 2.0;
  return (
    mod_x > (mid_divisor - stripe_width / 2.0) &&
    mod_x < (mid_divisor + stripe_width / 2.0)
  );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x / iResolution.x;
    float y = fragCoord.y / iResolution.y;
    if(is_stripe(y)) {
        fragColor = vec4(0.5, 0.5, 0.5, 1.0);
    } else {
        fragColor = vec4(0.7, 0.7, 0.7, 1.0);
    }
}
