void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -fragCoord+iResolution.xy/2.0;
    uv.y+=50.0;
    float x=uv.x;float y=uv.y; float t = iTime;
    x=x+x*sin(((degrees(atan(x*1.5,y))-180.0)/15.0+t*3.0)/3.0)/3.1416/2.0;
    y=y+y*cos(((degrees(atan(y,x*1.5)))/15.0+t*3.0)/3.0)/4.1416/2.0;
    fragColor=vec4(0.8);
    if(abs(x)<180.0&&abs(y-50.0)<120.0) {
        if (y-50.0>0.0) fragColor=vec4(1,1,0,1);
        else fragColor=vec4(0,0.5,1,1);
    }

}
