#define pi 3.141592653589793238462643383279
float speed = -70.0;
float scale = 1.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec3 color = 0.5 + 0.5 * sin((fragCoord.x * scale + iTime * speed + vec3(0.0, iResolution.x / 3.0, iResolution / 2.0)) * (pi / (iResolution.x / 2.0)));

    fragColor = vec4(color, 1.0);

}
