void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (-iResolution.xy + 3.0*fragCoord)/iResolution.y;


    float a = atan(p.y,p.x);

    vec2 uv = vec2( 1.0/a + 5.4*iTime, a );


    float f = sin(70.0*uv.x)*cos(6.0*uv.y);


    vec3 col = 0.5 + 0.4*sin( 23.0*f + vec3(0.0,2.5,4.0) );


    col = col+f;


    fragColor = vec4( col,1 );
}
