float distCircle(vec2 p, vec2 c, float r) {
 	 float dst = length(p - c);
     return  1.0 - clamp( dst/r,0.0,1.0);
}

float blockingCircle(float buffer,float circle){
  return max(buffer,circle);
}

float mergeCircle(float buffer,float circle)
{
 return buffer + circle;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/ iResolution.y;
    vec2 imouse = iMouse.xy;
    imouse /= iResolution.xy;
    imouse = imouse * 2.0 - 1.0;
    imouse.x *= aspect;

    vec2 c1 = vec2(0 , 0) ; 
    vec2 uv =   fragCoord.xy  /iResolution.xy ;
    uv = uv * 2.0 - 1.0;
    uv.x *= aspect; 

    float circle = distCircle(imouse,uv,0.8);
    circle = blockingCircle( circle ,distCircle(vec2(-0.3,0.5),uv,0.5));
    circle = mergeCircle( circle ,distCircle(vec2(-0.3,-0.5),uv,0.5));
    // Output to screen
    fragColor = vec4(circle ,  circle , circle,1.0);
}
