mat2 rotate(float angle) {
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

vec3 paintColor = vec3(0.0, 0.0, 0.0);

vec3 Rect(vec2 pos, float widthScale, float base, float offsetX, float offsetY, float angle) {
    pos = rotate(radians(angle)) * pos;
    return paintColor * ((max(abs((pos.x * (iResolution.x / iResolution.y) / widthScale) + offsetX), abs(pos.y + offsetY)) - base) < 0.0 ? 1.0 : 0.0); 
}

vec3 Circle(vec2 pos, float widthScale, float base, float offsetX, float offsetY, float angle) {
    pos.x += offsetX;
    pos.y += offsetY;
    pos = rotate(radians(angle)) * pos;
    pos.x -= offsetX;
    pos.y -= offsetY;
    return paintColor * (pow((pos.x  * (iResolution.x / iResolution.y) / widthScale) + offsetX, 2.0) + pow(pos.y + offsetY, 2.0) - pow(base, 2.0) < 0.0 ? 1.0 : 0.0);
}

float discr(float x) {
