const float PI = 3.14;

vec2 rotz(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

vec4 star(vec2 uv) {
    float z0 = smoothstep(0., 800., abs(1./uv.x) * abs(1./uv.y));
    float z1 = smoothstep(0., 4000., abs(1./uv.x) * abs(1./uv.y));
    vec4 c0 = vec4(0.76, 0.24, 0.08, 1.) * z0;
    vec4 c1 = vec4(1., 0.83, 0.52, 1.) * z1;
    return (c0 + c1) * .01/length(uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    fragColor = star(uv) + star(rotz(uv, PI/4.));
}
