// Modeling - 2022.02.06
// Eric Galin 

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Primitives 

// Sphere 
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Boite
// p : point
// b : taille
float Boite(vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// Tore
// p : point
// t :
float Tore( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// Cylindre
// p : point 
// a : 
// b : 
// r : rayon
float Cylindre(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

// Capsule
// p : point
// a :
// b : 
// r : 
float Capsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

// Plane 
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}

// Operators

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
    return min(a,b);
}

// Intersection
// a,b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}

// Difference
// a,b : field function of left and right sub-trees
float Difference(float a,float b)
{
    return max(a,-b);
}

// Rotation

// Matrice de rotation selon l'axe X
vec3 rotateX(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Matrice de rotation selon l'axe Y
vec3 rotateY(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Matrice de rotation selon l'axe Z
vec3 rotateZ(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

//Translation
vec3 Translation(vec3 p, vec3 t) {
    return p-t;
}

//Homotethie
vec3 Homotethie(vec3 p,float k)
{
    return p*k;
}

// Fonction colonne

float Colonne(vec3 p) {
    vec3 q = Translation(p, vec3(0,0,10));
    vec3 r = rotateY(p,11.0);
    vec3 s = Translation(p, vec3(0,0,-2.5));
    return Union(Union(Boite(s, vec3(2.0,2.0,1.0)),Cylindre(r,vec3(-9,0,0),vec3(2,0,0), 1.75)), Boite(q, vec3(2.0,2.0,1.0)));
}

float Escalier(vec3 p) {
    vec3 q = Translation(p, vec3(1,0,-3));
    vec3 q1 = Translation(p, vec3(3,0,-2));
    vec3 q2 = Translation(p, vec3(5,0,-1));
    vec3 q3 = Translation(p, vec3(7,0,0));
    return Union(Union(Union(Boite(q, vec3(2.0,5.0,1.0)),Boite(q1, vec3(2.0,5.0,1.0))),Boite(q2, vec3(2.0,5.0,1.0))),Boite(q3, vec3(2.0,5.0,1.0))); 
}

float Base(vec3 p) {
    vec3 q = Translation(p, vec3(-21,0,3));
    vec3 q1 = Translation(p, vec3(-19,0,2));
    float tmp = Difference(Boite(p,vec3(20,10,3)),Boite(q,vec3(10,5,5)));
    float tmp2 = Union(tmp,Escalier(q1));
    return tmp2;
}

float Toit(vec3 p) {
    vec3 t1 = Translation(p, vec3(-19,0,2));
    vec3 r1 = rotateY(p,4.0) - vec3(-1.1,0,-3.8) ;
    vec3 r2 = rotateY(p,2.28) - vec3(1.2,0,-3.85) ;
    return Union(Union(Boite(p,vec3(5.5,8.5,1)), Boite(r1,vec3(4,8.5,1))),Boite(r2,vec3(4,8.5,1)));
}

float Mur(vec3 p) {
    vec3 q = Translation(p, vec3(1,0,0));
    vec3 q1 = Translation(p, vec3(4,0,0));
    vec3 q2 = Translation(p, vec3(8,0,0));
    vec3 q3 = Translation(p, vec3(12,0,0));
    return Union(Union(Union(Colonne(p),Colonne(q1)),Colonne(q2)),Colonne(q3));
}
float Ceinture(vec3 p) {
    vec3 m = (Translation(p, vec3(4,4,3)))*2.4;
    vec3 m1 = Translation(m,vec3(-17,0,0));
    vec3 m2 = Translation(m,vec3(-17,-20,0));
    vec3 m3 = Translation(m,vec3(0,-20,0));
    vec3 r2 = rotateZ(m,1.57) - vec3(4,12,0);
    return Union(Union(Union(Union(Mur(m),Mur(m1)),Mur(m2)),Mur(m3)),Mur(r2));
}

float Porte(vec3 p) {
    vec3 p1 = (Translation(p, vec3(-3,2.5,3)))*2.4;
    vec3 p2 = Translation(p1,vec3(0,-12.8,0));
    float d = Union(Colonne(p1),Colonne(p2));
    return d;
}

// Potential field of the object
// p : point
float object(vec3 p)
{ vec3 r1 = (rotateZ(p,1.57) - vec3(0,2.3,8))*1.1;
  vec3 d = Homotethie(p, 2.0);
  float v = Union(Union(Union(Base(d),Toit (r1)),Ceinture(p)),Porte(p));
  v = Union(v,
      Intersection(
            Plane(p,vec3(0.0,0.0,1.0),vec3(0.0,0.0,-4.0)),
            Boite(p,vec3(100.0,100.0,5.0)))
     );

  return v;
}

// Analysis of the scalar field 

// Calculate object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps = 0.001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

  // Start at the origin
  float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v < 0.0)
      {
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Lighting 

// Ambient occlusion
// p : Point
// n : Normal
// r : Radius for ambient occlusion
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n, float r, int a)
{
    if (a==0) { return 1.0; }

	float ao=0.0; 

    for (int i=0;i<a;i++)
    {
 		vec3 d = Cosine(581.123*float(i) , n);

        int s;
        bool h;
        float t=SphereTrace(p,d,r,h,s);
        if (!h) {ao+=1.0;}
    }

    ao/=float(a);
	return ao;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.45,0.55,0.99),vec3(0.65,0.69,0.99), d.z*0.5+0.5);
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p+0.1*n,l,100.0,h,s);
    if (!h)
    {
      return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    // Ambient color
    vec3 ambient = 0.25+0.25*background(n);

    // Ambient occlusion
    ambient += 0.15 * AmbientOcclusion(p+0.1*n,n,5.0,0) * vec3(1.0,1.0,1.0);

    // Shadow computation
    float shadow = Shadow(p,n,l);

    // Phong diffuse
    vec3 diffuse = 0.35  * clamp(dot(n, l),0.0,1.0) * vec3(1.0,1.0,1.0);

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = 0.15 * pow(clamp(dot(r,l),0.0,1.0),30.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + shadow * (diffuse + specular);
    return c;
}

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t = float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p = (-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip = true;
    }
       else
       {
           pip = false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

// Modeling
// Eric Galin

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

// Cosine direction 
// seed : Random seed
//    n : Normal
vec3 Cosine( float seed, vec3 n)
{
    float u = Hash( 78.233 + seed);
    float v = Hash( 10.873 + seed);

    // Method by fizzer: http://www.amietia.com/lambertnotangent.html
    float a = 6.2831853 * v;
    u = 2.0 * u - 1.0;
    return normalize( n + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u) );
}

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(vec2 m, vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.5;

    // Origin
    ro=vec3(37.0,0.0,15.0);
    ro*=Rz(a); 

    // Target
    vec3 ta = vec3(0.0,0.0,1.0);

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
