float inHeart (vec2 uv, vec2 center, float size) {
  if (size==0.0) return 0.0;
  vec2 o = vec2((uv.x-center.x)/(1.2*size),(uv.y-center.y)/(1.6*size));
  float a = o.x*o.x+o.y*o.y-0.3;
  return step(a*a*a, o.x*o.x*o.y*o.y*o.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex1 = texture(iChannel1, uv);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = mix(tex0, tex1, inHeart(uv, vec2(0.5,0.25), abs(sin(iTime/1.50))));
    //fragColor = vec4(inHeart(uv, vec2(0.5,0.25), abs(sin(iTime/1.50))));
}
