float random (vec2 st) {
return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
vec2 u = 10. * fragCoord / iResolution.y - .05;
u.y += iTime*0.3;
float rnd = random(floor(u));
u = fract(u);
float angle = -sin(iTime * 1.5 * rnd*5.5)*1.8;
vec2 r = u + vec2(sin(angle),cos(angle)) * .3;
float c = length(r - .5) * 3.;
fragColor = vec4(smoothstep(c, c *1.2 ,0.6));
}
