#define TAU 6.28318530718
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 0.5-(fragCoord/iResolution.xy);
    uv *= 20.0;
    float t = 2.0*fract(iTime/2.0)-1.0;
    float x = uv.x;
    float y = uv.y;
    float X = sqrt(x*x+y*y);
    float Y = 3.0*atan(y,x);
    // Time varying pixel color
    float f = sin(X+Y+TAU*min(t,0.0))*sin(X-Y+TAU*max(t,0.0));
    f=smoothstep(-0.1,0.1,f);
    vec3 col = vec3(f);

    // Output to screen
    fragColor = vec4(col,1.0);
}
