void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float d = 0.0;
    for (int i = 1; i < 100; i ++) {
        float o = fract(iDate.w / float(i) * 1.0) * 3.14159 * 2.0;
        vec2 q = iResolution.xy / 2.0 + vec2(sin(o), cos(o)) * float(i) * 5.0;
        d += 1.0 / length(q - fragCoord);
    }
    fragColor = vec4(sin(vec3(10, 23, 15) / d), 1.0);
}
