vec4 lerp(vec4 a, vec4 b, float t)
{
    if(t <= 0.0)
        return a;
    if(t >= 1.0)
        return b;

    return a + (b - a ) * t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //消融速度
    float timeScale = 1.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //根据时间计算消融阈值
    float xTime = iTime;
    xTime *= timeScale;
    while(xTime > 5.0)
        xTime -= 5.0;
    float t = min(xTime/ 2.0, 3.1415926 / 2.0);
    float limit = sin(t);

    //噪声点
    float noise = texture(iChannel0, uv).r;


    if(noise > limit)
        fragColor = texture(iChannel1, uv);
    else
    {
        //边界颜色定义
        vec4 color1 = vec4(1.0, 1.0, 0.0, 1.0);
        vec4 color2 = vec4(0.7, 0.7, 0.0, 1.0);

        //边界计算
        limit -= 0.2;
        float tmp = noise - limit - xTime / 15.0;
        if(tmp > 0.0)
            fragColor = lerp(color2, color1, tmp * 10.0);
    }



}
