#define NUM_PARTICLES	75
#define NUM_FIREWORKS	1


vec3 saturate(vec3 v)
{
    vec3 ret = vec3(0.0);
    ret.x = min(1.0, max(0.0, v.x));
    ret.y = min(1.0, max(0.0, v.y));
    ret.z = min(1.0, max(0.0, v.z));

    return ret;
}

vec3 sun(vec2 sunPos, vec2 uv, float size, float haloSize)
{
    vec3 color = vec3(0.9, 0.8, 0.5);
    float term = 1.0/length(uv-sunPos) / 100.0 * size;
    vec3 ret = saturate(pow(color * term, vec3(2.0/haloSize)));
    return ret;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2. * fragCoord / iResolution.xy - 1.;
    float radio = iResolution.x / iResolution.y;
    uv.x *= radio;

    //left
    vec3 embient1 = vec3(0.6,0.6,0.9);
    vec3 sun1 = sun(vec2(-radio / 2.0, 0.0), uv, 5.0, 1.3) + embient1;

    //right
    vec3 embient2 = vec3(0.05,0.05,0.2);
    vec3 sun2 = sun(vec2(radio / 2.0, 0.0), uv, 10.0, 1.0) + embient2;

    fragColor = mix(vec4(sun1,1.),vec4(sun2,1.0), step(0.0, uv.x));
}
