void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 c = texture(iChannel0, uv);
    c.rgb = sin(c.rgb*(30.0+iMouse.y*80.0/iResolution.y)+iTime+vec3(50., 0.2,.5*texture(iChannel1, vec2(1.5*length(uv-.4),10.)).r))*.45+.5;
    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float z = (texture(iChannel2, vec2(length(uv-.5),1.)).r)*(20.0/iResolution.x*0.2+.001);
    float zoom = (1.-z);sin(iTime*20.)*.51+.9;
    uv-=.5;
    uv*=zoom;
    uv+=.5;
    vec4 o = texture(iChannel0, uv);
    vec4 c = texture(iChannel3,uv);
    c = mix(c,o,.95);
    fragColor = c;
}
