void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iChannelResolution[0].xy;
    uv /=  vec2(iChannelResolution[0].x/ iChannelResolution[1].x, iResolution.y/ float(NBUCKETS-1));
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.x > iChannelResolution[0].x ||fragCoord.y > float(NBUCKETS)) 
    {
        return;
    }

    float begin = (fragCoord.y/iChannelResolution[0].y) /  (float(NBUCKETS-1) /  iChannelResolution[0].y);
    float end = ((fragCoord.y+1.0)/iChannelResolution[0].y) /  (float(NBUCKETS-1) /  iChannelResolution[0].y);
    fragColor.rgb = vec3(0.0);
    for (int i = 0; i < int(iChannelResolution[0].y); ++i)
    {
        vec4 v = texelFetch(iChannel0, ivec2(fragCoord.x,i), 0);
        fragColor.r += step(v.r, end) * (1.0 - step(v.r, begin));
        fragColor.g += step(v.g, end) * (1.0 - step(v.g, begin));
        fragColor.b += step(v.b, end) * (1.0 - step(v.b, begin));
    }
    fragColor *= GAIN;

}

// Number of buckets must be lower than the y-size of view. Lower to improve performance and reduce precision.
#define NBUCKETS 256
#define GAIN 0.1
