const vec2 cell_size = 1. / vec2(3., 10.);
const float diagonal_slope = 0.45;
const float diagonal_offset = 0.6;

float hash1( uint n ) 
{
    // integer hash copied from Hugo Elias
    // implementation copied from https://www.shadertoy.com/view/llGSzw
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

vec4 image(vec2 uv)
{
    uv.y = 2.+uv.y;
    vec2 cell_uv;
    vec2 cell_fraction = modf(uv / cell_size, cell_uv);
    cell_uv = cell_uv * cell_size;


    // horizontal lines
    bool h = (abs(cell_fraction.y -.5)>0.4275);

    // vertical lines
    float tex = hash1(uint(cell_uv.y * 20. + (cell_uv.x + floor(uv.x))* 20.));

    float v_offset = 0.5 + tex /2.;
    float cell_d_x = diagonal_offset + (1.0 - cell_uv.y) * diagonal_slope;
    bool v = abs(cell_fraction.x - v_offset)<0.045;

    if(cell_fraction.x > v_offset)
    {
        cell_uv.x += cell_size.x;
        tex = hash1(uint(cell_uv.y * 20. + (cell_uv.x + floor(uv.x + cell_size.x))* 20.));
    }

    // diagonal line
    float patch_slope = diagonal_slope * (hash1(uint(uv.x))-.5);
    bool d = abs(diagonal_offset - fract(uv.x) + patch_slope * (1.0 - uv.y)) < 0.03;

    vec3 col = h || v || d ? vec3(0.0) : vec3(tex);

    return vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // dirty zoom
    uv += 0.5;
    uv *= 0.5;

    // basic perspective from https://www.shadertoy.com/view/ttscW2
    float coeff =0.25 *sin(iTime/5.)+0.75;

    uv.x -= 0.5f;
	uv.x /= (1.0 - uv.y * coeff);
    uv.y /= (1.0 - uv.y * coeff);

    uv.y += iTime/10.;
    uv.x += sin(iTime/5.)/10.;
    // Output to screen
    fragColor = image(uv.yx);
}
