void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    uv *=5.5;     

    vec3 col1 = vec3(3.0,0.0,1.0);      

    vec3 color = vec3(sin(uv.y * 50. + iTime) * 0.5)*0.1
                      + sin(uv.y * 50. + iTime*0.5) * 0.1
                      + sin(uv.x * 50. + 0.5) * 0.1;   


    float r = min(abs(0.5/uv.y), abs(0.5*uv.x));     


    vec2 uvWave = uv;
    uvWave.y += sin(uv.x * 10. + iTime) * 0.3 
                + sin(uv.x * 0. + iTime*2.5) * 0.3
                + sin(uv.x * 10. + iTime*2.5) * 0.3
                + sin(uv.x * 2000. + iTime*-2.5) * 0.3;


    //uvWave.y += sin(uv.y*1.5+iTime) * 0.8;
    //uvWave.y += sin(uv.y*2.0)*0.1;

    float waves = step(uvWave.y , 0.9);
    float waves2 = step(uvWave.y , 0.5);

    //float p = step(uv.x+sin(uv.y*1.0)* 0.2+sin(uv.y*5.0)*0.5,5.5);


    color+= sin(uv.x * -5. + iTime) * 0.5;


    color += mix(vec3(0.1), vec3(1.0,1.0,1.6), r*waves*waves2) * step(r,iTime);     


    // Output to screen
    fragColor = vec4(color,1.0);
}
