uvec3 murmurHash32(uvec2 src) {
    const uint M = 0x5bd1e995u;
    uvec3 h = uvec3(1190494759u, 2147483647u, 3559788179u);
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src.x; h *= M; h ^= src.y;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}

vec3 hash32(vec2 src) {
    uvec3 h = murmurHash32(floatBitsToUint(src));
    return uintBitsToFloat(h & 0x007fffffu | 0x3f800000u) - 1.0;
}
vec2 cell(vec2 v)
{
    v.y*=1.1547;
    v.x-=v.y*.5;
    vec2 f = floor(v);
    vec2 c = fract(v);

    float mx = mod(f.x,3.0);
    if(mx==0.0)
    {
        f.y-=2.0;
    }
    else if(mx==2.0)
    {
        f.y+=2.0;
    }
    float my = mod(f.y,3.0);
    if(my==0.0)
    {
        f.x += 1.0;
    }
    else if(my==1.0)
    {
        f.y += 1.0;
    }
    else if(c.x+c.y>1.0)
    {
        f.x += 1.0;
        f.y += 1.0;
    }
    if(mx==0.0)
    {
        f.y+=2.0;
    }
    else if(mx==2.0)
    {
        f.y-=2.0;
    }

    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(2.0*fragCoord-iResolution.xy)/iResolution.y;

    fragColor = vec4(hash32(cell(uv)),1.0);
}
