void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    //channel offset
    vec4 tex0 = texture(iChannel0, uv);
    tex0.r = texture(iChannel0, vec2(uv.x/(1.0055+0.005*sin(iTime*3.)), uv.y)).r;
    tex0.g = texture(iChannel0, vec2(uv.x/(1.0045-0.02*(cos(iTime))), uv.y)).g;




    // Output to screen
    fragColor = tex0;
}
