void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

    vec4 leftcolor = vec4(1.0,0.0,0.0,1.0);
    vec4 rightcolor = vec4(0.0,1.0,0.906,1.0);
    float abbdist = 4.0;

    // Normalized pixel coordinates (from 0 to 1)

    //scrolly cat and wave
    vec2 uv = fragCoord/iResolution.xy;

    vec4 c = texture(iChannel1, uv);

    vec4 outc = c;

    fragCoord.x += abbdist;

    uv = fragCoord/iResolution.xy;
    c = texture(iChannel1, uv);

    if(c.a != 0.0){
        outc = leftcolor;
    }
    fragCoord.x -= abbdist * 2.0;

    uv = fragCoord/iResolution.xy;
    c = texture(iChannel1, uv);

    if(c.a != 0.0){
        outc = rightcolor;
    }

    fragCoord.x += abbdist;

    uv = fragCoord/iResolution.xy;
    c = texture(iChannel1, uv);
    if(c.a != 0.0){
        outc = c;
    }

    fragColor = outc;
}
