vec2 hash22(vec2 p) {
    // credit to Dave_Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float lengthSqr(vec2 v) {
    return dot(v, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    const float scrambleFreq = 2.;
    const float uvTiling = 3.;

    vec2 uv = scrambleFreq*uvTiling*fragCoord/iResolution.yy;

    vec2 uvFloor = floor(uv);
    vec2 uvFrac = uv - uvFloor;

    vec2 splatCenters[2];
    splatCenters[0] = uvFrac.x < 1. - uvFrac.y ? vec2(0., 0.) : vec2(1., 1.);
    splatCenters[1] = uvFrac.x < uvFrac.y      ? vec2(0., 1.) : vec2(1., 0.);
    splatCenters[0] += uvFloor;
    splatCenters[1] += uvFloor;

    vec2 weights;
    weights[0] = max(1. - 2.*lengthSqr(uv - splatCenters[0]), 0.);
    weights[1] = max(1. - 2.*lengthSqr(uv - splatCenters[1]), 0.);
    weights /= dot(weights, vec2(1.));

    float tweak = smoothstep(-0.1, 0.1, cos(iTime));

    vec3 col;

    // explicit gradients for consistent mip selection
    uv /= scrambleFreq;
    vec2 duv_dx = dFdx(uv);
    vec2 duv_dy = dFdy(uv);
    col  = weights[0]*textureGrad(iChannel0, uv + tweak*(hash22(splatCenters[0]) - 0.5), duv_dx, duv_dy).rgb;
    col += weights[1]*textureGrad(iChannel0, uv + tweak*(hash22(splatCenters[1]) - 0.5), duv_dx, duv_dy).rgb;

#if 1
    // contrast correction, hat tip to Fabrice - https://www.shadertoy.com/view/lsKGz3
    vec3 mean = texture(iChannel0, uv, 16.).rgb;
    col = mean + (col - mean)/length(weights);
#endif

#if 0
    // visualize blend kernel
    col.rg = hash22(splatCenters[0])*weights[0] + hash22(splatCenters[1])*weights[1];
#endif

    fragColor = vec4(col,1.0);
}
