vec2 R( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 O, vec2 I )
{
    vec2 S = I.xy/iResolution.xy;
    S.x *= iResolution.x/iResolution.y;
    vec3 c = vec3(.0);
    S *= 5.;

    vec2 k = floor(S);
    vec2 f = fract(S);

    float M = 10.;  
    vec2 m;        

    for (int j=-1; j<=1; j++ ) {
        for (int i=-1; i<=1; i++ ) {
            vec2 n = vec2(float(i),float(j));
            vec2 p = R(k + n);
            p = 0.5 + 0.5*sin(iTime + 6.2831*p);
            vec2 d = n + p - f;
            float D = length(d);

            if( D < M ) {
                M = D;
                m = p;
            }
        }
    }
    c += dot(m,vec2(.3,.6));
    vec3 midnight = vec3(0.173, 0.243, 0.314);
	c *= midnight;

    O.xyz = c;
}
