#define F(x,y) col = mix(x, col, smoothstep(0., fwidth(y), y))

vec4 mix4(vec4 v1, vec4 v2, float a) { return v1 * (1. - a) + v2 * a; }

vec4 getNote(int n, int channel) {
    return texelFetch(iChannel1, ivec2(n, channel), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.x *= SCALE;

    vec3 col = vec3(0.02);
    vec3 effects = vec3(0);

    for(int channel = 0; channel < 6; channel++) {
        ivec2 index = ivec2(texelFetch(iChannel0, ivec2(channel, 0), 0).xy);

        for(int i = 0; i < index.y - index.x; i++) {
            int noteIndex = index.x + i;

            vec4 noteBox = getNoteBox(getNote(noteIndex, channel), iTime);
            vec4 nextNoteBox = getNoteBox(getNote(noteIndex + 1, channel), iTime);

            float d = sdNote(noteBox.xy - uv, noteBox.zw * 1.5) + .01;
            //vec3[6](vec3(1.000,0.000,0.000),vec3(1.000,0.000,0.918),vec3(0.114,0.000,0.980),vec3(0.000,1.000,0.384),vec3(1.000,0.969,0.000),vec3(0.867,0.478,0.031))[channel]
            vec3 nCol = hsv2rgb(vec3(float(channel+1)/6.,.76,1.));
            F(nCol, d);
            //col = mix(col, vec3[6](vec3(1.000,0.000,0.000),vec3(1.000,0.000,0.918),vec3(0.114,0.000,0.980),vec3(0.000,1.000,0.384),vec3(1.000,0.969,0.000),vec3(0.867,0.478,0.031))[channel], float(d<=0.));
            if(noteBox.x - noteBox.z < 0. && noteBox.x + noteBox.z > 0.) F(pow(col,vec3(0.05)), d);

            float t = 1. - (nextNoteBox.x - nextNoteBox.z) / (nextNoteBox.x - noteBox.x + noteBox.z - nextNoteBox.z);
            vec4 lerped = mix4(noteBox, nextNoteBox, t);
            effects = mix(effects, float((nextNoteBox.x - nextNoteBox.z) - (noteBox.x + noteBox.z) <= 0.5) * pow(col,vec3(0.2)), float(sdNote(lerped.xy - uv,lerped.zw) <= 0. && (noteBox.x - noteBox.z < 0. && nextNoteBox.x - nextNoteBox.z > 0.)));
        }
    }
    col += effects;

    fragColor = vec4(col,1.0);
}

vec4 getNote(int n, int channel) {
    return texelFetch(iChannel1, ivec2(n, channel), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int channelIndex = ivec2(fragCoord).x;
    vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);
    int left = 0;
    int right = 1;
    if(channelIndex < 6) {
        left = int(data.x);
        right = int(data.y);

        vec4 box = getNoteBox(getNote(left, channelIndex), iTime);
        if(box.x + box.z < -.5 * SCALE * ASPECT) left += 1;

        box = getNoteBox(getNote(right, channelIndex), iTime);
        if(box.x - box.z < .5 * SCALE * ASPECT && iTime < 490.) right += 1;
    }
    fragColor = vec4(left, right, -1, -1);
}

#define CENTER 60.
#define BPM 50.
#define SCALE 5.
#define ASPECT 1.7777


float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec4 getNoteBox(vec4 note, float t) {
    t -= 1.5;
    float m = 60. / BPM;
    note.xz *= m;
    float interval = .8 / 88.; 
    return vec4(note.x + note.z / 2. - t,                        
               (note.y - CENTER) * interval * 2. - interval / 2.,
                note.z / 2.,
                interval * 2.);
}

float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }
float sdRhombus( in vec2 p, in vec2 b ) 
{
    p = abs(p);
    float h = clamp( ndot(b-2.0*p,b)/dot(b,b), -1.0, 1.0 );
    float d = length( p-0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( p.x*b.y + p.y*b.x - b.x*b.y );
}

float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float sdNote(in vec2 p, in vec2 wh) {


    return sdRhombus(p, wh);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
