const float pi = 3.1415926;
const float pi2 = 2.*pi;
const float rad = pi/180.;


mat2 rotate(float x){
    float c = cos(x);
    float s = sin(x);
    return mat2(c,s,-s,c);
}

bool snow = false;
bool optimized = false;

float dist(vec3 p)
{
    float d = 1.0;

    vec2 ps = ((p.xy+vec2(3., 2.)))/1.0;
    vec3 c  =  texelFetch( iChannel0, ivec2(ps*100.), 0).rgb;
    vec3 c2 =  texelFetch( iChannel0, ivec2(ps*20.+.5)*5, 0).rgb;

    float f = .1;


    vec2 v = cos(c.xy*.3);
     v = (1.-c.xy*.1);

    // d = .1;
    vec2 v2 = (1.-c2.xy*.1);;
    vec2 vp = floor(ps*2.+.5);

    vec2 m2 = mod(vp,2.0);


    if (snow) {
        d = min(d, 0.001 +length(p-vec3(p.x,p.y,+ v.x+1.0)));
    }
    else{
        if (optimized){
            vec3 c1 = vec3(.05);
            vec3 q = mod(p+0.5*c1,c1)-0.5*c1;
            q.z=p.z;
            d = min(d, -.001+length(q-vec3(0.0, 0.,v2+1.))*1.0);
        }
    }


    d = min(d, 1.- p.z + v.y);

    return d;
}

vec3 getNormal(vec3 p)
{
    vec2 d = vec2(0., 0.01);
    float x = dist(p-d.yxx);
    float y = dist(p-d.xyx);
    float z = dist(p-d.xxy);
    return normalize(vec3(x,y,z)-dist(p));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 status = texelFetch( iChannel0, ivec2(0.0), 0);

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvp = uv * vec2(iResolution.x/iResolution.y, 1.0);

    vec3 c = vec3(0.0);

    float my = iMouse.y/iResolution.y;
    if (my <= 0.0)
        my = .5;

    c = texture(iChannel0, uv).rgb;

    fragColor = vec4(c.y*0.1+.2);

    if (uv.y > my){ 
        fragColor = mix(fragColor, vec4(c.x*0.1+.2), 0.1*max(0., -cos(iTime*.5)));
    }

    fragColor = mix(fragColor, vec4(1.,0.,0.0,0.),(smoothstep(0.005,.0, abs(uv.y-my)))*.5);


     uvp *= 40.0;
    fragColor = mix(fragColor, vec4(.5), .3*smoothstep(0.1,-0.2, min(abs(mod(uvp.x, 1.)-.5) , abs(mod(uvp.y, 1.) -.5 ))) );


    /////////////////////////////
    float a = iTime*.5;
    snow = cos( a*1.0 ) <  -.341592 ;          

    optimized = cos( a*2.0 ) <  -.141592 ;

    if (status.x == 1.0){
        snow = false;
        optimized = false;
    }
    if (status.x == 2.0){
        snow = true;
        optimized = false;
    }            
    if (status.x == 3.0){
        snow = false;
        optimized = true;
    }
    /////////////////////////////


    if (uv.x > .5){
        c = texelFetch( iChannel0, ivec2((uv.x-.5)*200., my), 0).rgb;
        c = texelFetch( iChannel0, ivec2(((uv.x-0.5)*iResolution.x), my*iResolution.y), 0).rgb;
        //c = texture(iChannel0, vec2((uv.x-.5)*1., my), 0.).rgb;

        fragColor = vec4(1.0);

        float y= uv.y*2.0;
        float k = .1;
        fragColor = mix(fragColor, vec4(0.),(smoothstep(0.01,.0, abs(c.x*k-y))));
        fragColor = mix(fragColor, vec4(1.,0.,0.0,0.),(smoothstep(0.01,.0, abs(c.y*k-y))));

        uvp = fragCoord/4.0;
        fragColor = mix(fragColor, vec4(.5), .3*smoothstep(0.4,-0.4, min(abs(mod(uvp.x, 1.)-.5) , abs(mod(uvp.y, 1.) -.5 ))) );

        if (uv.y > .5){

            vec2 uv = fragCoord/iResolution.xy;
            vec2 pc = (uv-.75)*vec2(1, iResolution.y/iResolution.x);

            vec3 p = vec3(0.);
            p += vec3(0.,-5.0,-5.);
            float sr = 5.;


            if (status.z > 0.0)
                a = -status.z;


            p = vec3(sr*cos(a)-.85,sr*sin(a), 0.2);
            vec3 d = normalize(vec3(pc,1.0));
            d.yz *= rotate(1.341592);
            d.xy *= rotate(-a+4.63);

            vec3 c=vec3(0.0);
            float t = 0.;
            for (int i = 0 ; i < 100; i++)
            {
                float h=dist(p); 
                float h2=dist(p+ d*h);
                h = (h+h2)/2.0;
                if (h < 0.01)
                 break;
                t+=h;
                p += d*h;
            }

            c += getNormal(p).z;
            if (snow)
                c +=.4;
            fragColor = vec4(c,1.0);

            p *= 10.0;
            fragColor = mix(fragColor, vec4(0.0), smoothstep(0.15,-0.0-t*0.1, min(abs(mod(p.x, 1.)-.5) , abs(mod(p.y, 1.) -.5 ))) );

        }  
    }

    /////////////////////////////
    // radio button

    vec3 buttonCor = vec3(0.);
    fragCoord -=vec2(0., iResolution.y);
    fragCoord *= vec2(1.0,-1.0);
    vec2 posButton = fragCoord - vec2( iResolution.x/4., 0.);;


    fragColor.rgb = mix(fragColor.rgb, 1.- buttonCor, smoothstep(.0,2.0, 20.-length( ( max(vec2(0.), abs(posButton-vec2(65.0, 15.0)) - vec2(60.,0.0)))  ) ));
    fragColor.rgb = mix(fragColor.rgb, 1.- buttonCor, smoothstep(.0,2.0, 20.-length( ( max(vec2(0.), abs(fragCoord-vec2(5.0, 15.0)) - vec2(60.,0.0)))  ) ));


    for (float i = 0.0; i < 4.0; i++){
        fragColor.rgb = mix(fragColor.rgb, buttonCor, smoothstep(2.0,.0,  abs(10. - ((length(posButton-vec2(20.+30.0*i,15.0)))))));
        if (status.x == i) fragColor.rgb = mix(fragColor.rgb, buttonCor, smoothstep(2.0,.0, -(5. - ((length(posButton-vec2(20.+30.0*i,15.0)))))));
    }

    float k = 0.0;
    if (snow) k = 1.0;
    if (optimized) k = 2.0;
    fragColor.rgb  = mix(fragColor.rgb, vec3(0.,0.,1.0), smoothstep(2.0,.0,  abs(13. - ((length(posButton-vec2(20.+30.0*(k+1.0),15.0)))))));

    for (float i = 0.0; i < 2.0; i++){
        vec2 bp = vec2(20.+30.0*i,15.0);
        vec2 bpc = fragCoord-bp;
        fragColor.rgb = mix(fragColor.rgb, buttonCor, smoothstep(2.0,.0,  abs(10. - length(bpc))));
        if (status.y == i) fragColor.rgb = mix(fragColor.rgb, buttonCor, smoothstep(2.0,.0, -(7. - ((length(bpc))))));
        fragColor.rgb = mix(fragColor.rgb, (status.y == i) ? vec3(0.5,0.5, 1.0) : vec3(0.0), smoothstep(2.0,.0, max(-(4. - length(bpc)) , min(-1.+abs(bpc.y), -1.+abs(bpc.x)+(1.0-i)*100.0) ) ) );
    }
}

