//https://iquilezles.org/articles/distfunctions2d
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
//////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////////

// ---------------------------------------------------------------//


vec4 getTexture(sampler2D s,  vec2 uv){
    return texelFetch( s, ivec2(uv), 0);
  //  return texture(s, uv/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float s = 0.01;

    vec2 uv = fragCoord/iResolution.xy;
    float rnd = 2.+ abs(fract(dot((uv+cos(uv.x+uv.y*10.0+iTime*1.0+cos(uv.x*123.0)))*179.531,  vec2(1324.1327,1.1323))))*4.0;
    uint strChar[] = uint[1] (1u);

    vec3 v = vec3( 1.0, -1.0, 0.0);

    /**
    vec2 d[] = vec2[] (v.zz, 
      v.xz, -v.xz, v.zx, -v.zx,
      v.xx,v.xy,v.yx,v.yy
    );
      const int len = d.length();
    /**/

    vec2 d[9];
    d[0] = v.zz;
    d[1] = v.xz;
    d[2] = -v.xz;
    d[3] = v.zx;
    d[4] = -v.zx;
    d[5] =  v.xx;
    d[6] =  v.xy;
    d[7] =  v.yx;
    d[8] =  v.yy; 
    const int len = 9;


    vec4 status = getTexture(iChannel0, vec2(0.0));


    uv = fragCoord; 
    vec4 b1 = getTexture(iChannel0, uv);
    vec4 b2 = getTexture(iChannel0, uv+v.xz);
    vec4 b3 = getTexture(iChannel0, uv-v.xz);
    vec4 b4 = getTexture(iChannel0, uv+v.zy);
    vec4 b5 = getTexture(iChannel0, uv-v.zy);
    fragColor = b1;



    float minValue = 1000.0;
    int minIndex = 0;

    float maxValue = -1000.0;
    int maxIndex = 0;

    bool collision ;
    vec4 back;


    for (int i = 0; i < len; i++){

         vec2 r = d[i];
         vec4 b = getTexture(iChannel0, uv+r); // x - fall, y - floor


         if (b.x <= b.y){
            collision = true;
         }

         if (b.y < minValue ){
            minValue = b.y;
            minIndex = i;
            back = b;
         }

          if (b.y > maxValue ){
            maxValue = b.y;
            maxIndex = i;
            back = b;
         }

    }

    vec2 rMin = d[minIndex];
    vec4 bMin = getTexture(iChannel0, uv+rMin); // x - fall, y - floor

    vec2 rMax = d[maxIndex];
    vec4 bMax = getTexture(iChannel0, uv+rMax); // x - fall, y - floor


    float sb =  v.y*1.0;
    if (uv.x < sb || uv.x > 1.0- sb ||  uv.y < sb || uv.y > 1.0- sb)
         collision = (fragColor.x <= fragColor.y);


    if (iFrame==0 ){

        fragColor.x = rnd;
        fragColor.y = max(0., cos((uv.x)*0.1) + cos((uv.y)*0.1)+ 1.-length(uv/iResolution.yy-.5)*12.5);
        fragColor.y = max(fragColor.y, smoothstep(0.01,0.0, sdBox(uv/iResolution.yy-.5, vec2(.2))));

        fragColor.z = 1.0;


    } 
    else if (false){
        if ((fragColor.x <= fragColor.y )){
            fragColor.x = rnd;
            fragColor.y += s * fragColor.z;
            fragColor.z  = 1.0;
        }
        else
        {
             fragColor.x -= s;
        }
    }
    else if (collision)
    {

        if (minIndex == 0){
            fragColor.x = rnd;
            fragColor.y += s * fragColor.z*10.0;
            fragColor.z = 1.0;
        }
        else 
        if ((fragColor.y <= (bMax.y - bMin.y)/2.0 + bMin.y )){

            fragColor.x = rnd;
            fragColor.y += s * fragColor.z*10.0 * (bMax.y - bMin.y)/2.0;
            fragColor.z = 1.0;
        }

        fragColor.x = rnd;

    }
    else
    {
        fragColor.x -= s;
    }

    fragColor.y = min(fragColor.y, 8.0);
    if (fragColor.y > 9.0)
        fragColor.y *= .9999;

    if (sign(iMouse.z) > 0.0){
        if (iMouse.xy != iMouse.zw){
            fragColor.y = max(0., fragColor.y + sign(status.y-.5)*max(0., 2. - ((length(uv-iMouse.xy)*.04)))*0.008);
        }
    }


    if (fragCoord.y <= 1.) {
        fragColor = getTexture(iChannel0, fragCoord);

        if (sign(iMouse.z) > 0.0 && sign(iMouse.w) > 0.0){
            vec2 mouse = iMouse.xy;
            mouse  -=vec2(0., iResolution.y);
            mouse *= vec2(1.0,- 1.0);

            vec2 posButton = fragCoord + vec2( iResolution.x/4., 0.);;

            for (float i = 0.0; i < 4.0; i++){
               if (smoothstep(2.0,.0,  -(10. - ((length(posButton-mouse+vec2(20.+30.*i,15.0)))))) > 0.5) fragColor.x = i;
            }
            for (float i = 0.0; i < 2.0; i++){
               if (smoothstep(2.0,.0,  -(10. - ((length(fragCoord-mouse+vec2(20.+30.*i,15.0)))))) > 0.5) fragColor.y = i;
            }
        }

        vec2 m = iMouse.xy/iResolution.xy;
        if (m.x > .5)
        {
            if (m.y > .5)
            {
                 fragColor.z = m.x*13.0;
            }else
            {
                 fragColor.z = 0.0;
            }
        }

    }

}
