#define PI 3.14159265
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    float radius = fract(iTime*0.5);
    float dis = length(uv);
    float bate = atan(uv.y, uv.x) + radians(1.-fract
    (iTime*0.5 ) ) * 200. * (radius - dis) / radius ;
    if(dis <= radius) {
        uv = dis * vec2(cos(bate), sin(bate));
         fragColor = texture(iChannel1, uv - 0.5);
    }else{
        fragColor = texture(iChannel0,  uv - 0.5);
    }

}
