float treshold = -4.0;//边缘厚度
float range = 1.2;
vec3 lightColor = vec3(1.0,0.99,0.81);//rgb
float intensity = 2.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv0 = fragCoord/iResolution.xy;

  vec2 uv1 = uv0;
  vec2 screen = vec2(iResolution.x, iResolution.y);

  float color = 0.0;
  //shadertoy只有绿幕的视频，这段代码应该给背景为透明的视频来使用
  color +=  texture(iChannel0, uv1 + vec2(treshold, 0) / screen).r;
  color +=  texture(iChannel0, uv1 + vec2(0, treshold) / screen).r;
  color +=  texture(iChannel0, uv1 + vec2(-treshold, 0) / screen).r;
  color +=  texture(iChannel0, uv1 + vec2(0, -treshold) / screen).r;
  color = clamp(color, 0.0, 1.0) * intensity;
  color = clamp(color, 0.0, 1.0);

  fragColor = mix(vec4(lightColor, 1.0) * range , vec4(0.0), texture(iChannel0, uv1).r);
  fragColor = mix(vec4(0.0), fragColor, color);
}
